import { UserContext } from '../contexts/user.context';
import {useEffect, useState } from "react";
import { useParams, useNavigate, Link as RouterLink } from 'react-router-dom';
import { TextField, Button, Typography, FormLabel, Box, FormControlLabel, RadioGroup, Radio } from '@mui/material'

export default function Todo() {
  const [form, setForm] = useState({
    name: "",
    position: "",
    level: "",
  });
  const [isNew, setIsNew] = useState(true);
  const params = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchData() {
      const id = params.id?.toString() || undefined;
      if (!id) return;
      setIsNew(false);
      const response = await fetch(`http://localhost:5050/todo/${id}`);
      if (!response.ok) {
        console.error(`An error has occurred: ${response.statusText}`);
        return;
      }
      const todo = await response.json();
      if (!todo) {
        console.warn(`Todo with id ${id} not found`);
        navigate("/");
        return;
      }
      setForm(todo);
    }
    fetchData();
  }, [params.id, navigate]);

  function updateForm(value) {
    setForm((prev) => ({ ...prev, ...value }));
  }

  async function onSubmit(e) {
    e.preventDefault();
    const item = { ...form };
    try {
      let response;
      if (isNew) {
        response = await fetch("http://localhost:5050/todo", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(item),
        });
      } else {
        response = await fetch(`http://localhost:5050/todo/${params.id}`, {
          method: "PATCH",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(item),
        });
      }
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      navigate("/");
    } catch (error) {
      console.error('A problem occurred adding or updating a todo: ', error);
    } finally {
      setForm({ name: "", position: "", level: "" });
      navigate("/");
    }
  }

  return (
    <Box sx={{ p: 4 }}>
      <Typography variant="h6" sx={{ mb: 2 }}>
        Create/Update Employee Todo
      </Typography>
      <Box component="form" onSubmit={onSubmit} noValidate sx={{ mt: 1 }}>
        <TextField
          variant="outlined"
          margin="normal"
          required
          fullWidth
          id="name"
          label="Name"
          name="name"
          autoComplete="name"
          autoFocus
          value={form.name}
          onChange={(e) => updateForm({ name: e.target.value })}
        />
        <TextField
          variant="outlined"
          margin="normal"
          required
          fullWidth
          id="position"
          label="Position"
          name="position"
          autoComplete="position"
          value={form.position}
          onChange={(e) => updateForm({ position: e.target.value })}
        />
        <FormLabel component="legend">Level</FormLabel>
        <RadioGroup
          row
          aria-label="level"
          name="level"
          value={form.level}
          onChange={(e) => updateForm({ level: e.target.value })}
        >
          <FormControlLabel value="Intern" control={<Radio />} label="Intern" />
          <FormControlLabel value="Junior" control={<Radio />} label="Junior" />
          <FormControlLabel value="Senior" control={<Radio />} label="Senior" />
        </RadioGroup>
        <Button
          type="submit"
          fullWidth
          variant="contained"
          sx={{ mt: 3, mb: 2 }}
        >
          Save Employee Todo
        </Button>
      </Box>
    </Box>
  );
}
